sp_widget.client_script:
api.controller=function($rootScope,$scope, spUtil, spModal) {
	/* widget controller */
	var c = this;
	console.log('start', c.data.notes)
	spUtil.addInfoMessage("welcome to the My hello world 1 widget!!");
	c.selectItem = function(idx) {
		var id = c.data.notes[idx].sys_id;
		console.log('Note ID: ' + id);
		$rootScope.noteID = id;
		$rootScope.$emit('selectNote', id);
		console.log(id)
		console.log()
	}

	c.addItem = function(){
		spModal.confirm("Are you sure you want to add this Note record?")
			.then(function(confirmed){
			if(confirmed){
				console.log('add item?');
				c.server.get({
					action: 'addNote'
				}).then(function(r) {});
			}
		});
	}
}

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	data.notes = [];
	var noteGR = new GlideRecord('x_snc_createnotes_note');
	//gs.info(gs.getUser().getFullName())
	noteGR.addQuery('user', gs.getUser().getID());
	noteGR.orderByDesc('sys_created_on');
	//gs.addInfoMessage(options.maximum_records_to_display);
	noteGR.setLimit(options.maximum_record_to_display)
	noteGR.query();
	//gs.addInfoMessage("its running list!")
	while (noteGR.next()) {
		var noteObj = {};
		//use service portal helper method to get some display values
		$sp.getRecordDisplayValues(noteObj, noteGR, 'number,title,sys_id');
		//get the first 20 characters of the description
		noteObj.note = noteGR.getValue('note').slice(0,20);
		//push the populated obj into the array
		data.notes.push(noteObj);
	}
	//gs.info('data.notes')
	if(input){
		if(input.action == 'addNote'){
			//gs.addInfoMessage('add item.');
			var now_GR = new GlideRecord('x_snc_createnotes_note');
			now_GR.initialize();
			now_GR.setValue('title', 'New Record');
			now_GR.setValue('note', 'Note of the Record');
			now_GR.setValue('user', gs.getUser().getID());
			gs.addInfoMessage(now_GR.insert());

		}
	}

})();
